package de.fom.kdp.project.lib.peoplegeneratorDemoProjekt;

import java.io.IOException;

import de.fom.kdp.project.lib.peoplegeneratorapi.Peoplegeneratorapi;

/**
 * The {@code DataSet} class is responsible for holding and managing data
 * related to people generated by the People Generator API. It provides methods
 * to read data from the API, prepare it for processing, and generate statistics
 * based on the data.
 * 
 * <p>
 * <strong>Note:</strong> This class is marked as {@link Deprecated} and may be
 * removed or replaced in future versions.
 * </p>
 * 
 * @author engels
 * @deprecated This class is deprecated and may be removed or replaced in future
 *             versions.
 */
@Deprecated
public class DataSet {
	int anz = 0;
	String jsondata = null;
	PeopleStats peopleStats;

	/**
	 * Constructs a {@code DataSet} instance with the specified number of people to
	 * be fetched from the API.
	 * 
	 * @param anz the number of people to generate data for.
	 */
	public DataSet(int anz) {
		this.anz = anz;
	}

	/**
	 * Reads data from the People Generator API and stores it as a JSON string.
	 * 
	 * @return the JSON string containing people data, or {@code null} if an error
	 *         occurs during data retrieval.
	 */
	public String readData() {
		jsondata = null;
		try {
			jsondata = Peoplegeneratorapi.getPeople(anz);
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} catch (InterruptedException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		return jsondata;
	}

	/**
	 * Cleans up and prepares the fetched data for further processing.
	 * <p>
	 * This method is currently not implemented and is intended for future use.
	 * </p>
	 */
	public void prepareData() {

	}

	/**
	 * Generates statistics based on the fetched people data.
	 * 
	 * @return a {@code PeopleStats} object containing statistics about the people
	 *         data.
	 */
	public PeopleStats makeStats() {

		peopleStats = new PeopleStats(jsondata);

		return peopleStats;

	}

}
